// Helper function for delete confirmation message
func getDeleteConfirmMessage(lang string) string {
	if lang == "ar" {
		return "هل أنت متأكد من حذف هذه الورشة؟ لا يمكن التراجع عن هذا الإجراء."
	}
	return "Are you sure you want to delete this workshop? This action cannot be undone."
}

// Helper function to escape JavaScript strings
func escapeJS(s string) string {
	s = strings.ReplaceAll(s, "\\", "\\\\")
	s = strings.ReplaceAll(s, "'", "\\'")
	s = strings.ReplaceAll(s, "\"", "\\\"")
	s = strings.ReplaceAll(s, "\n", "\\n")
	s = strings.ReplaceAll(s, "\r", "\\r")
	s = strings.ReplaceAll(s, "\t", "\\t")
	return s
}

// Helper function to convert sessions to JavaScript
func convertSessionsToJS(sessions []models.WorkshopSession) string {
	if len(sessions) == 0 {
		return `{id: null, date: '', time: '', duration: 2}`
	}

	var jsData []string
	for _, session := range sessions {
		js := fmt.Sprintf(`{
            id: '%s',
            date: '%s',
            time: '%s',
            duration: %.1f
        }`,
			session.ID.String(),
			session.SessionDate.Format("2006-01-02"),
			session.StartTime,
			session.Duration)
		jsData = append(jsData, js)
	}

	return strings.Join(jsData, ",\n")
}

